const initialState = {
    isAuthenticated: false,
    userData: null,
  };

  const userReducer = (state = initialState, action) => {
    switch (action.type) {
      case 'SET_USER_DATA':
      return {
        ...state,
        isAuthenticated: true,
        userData: action.payload,
      };
      default:
        return state;
    }
  };

  export default userReducer;
